import { useContext } from "react";
import { useParams } from "react-router-dom";
import { PokemonContext } from "../data/pokeContext";
import PokeType from "../components/pokeType";


function PokemonPage() {
    const data = useContext(PokemonContext);
    const {id} = useParams();

    const pokemon = data[id - 1];
    const imgPath = pokemon.sprites.regular

    console.log(pokemon);

    return(
        <div className="h-screen w-screen flex justify-center items-center bg-[url('./assets/forest.jpeg')]">
            <div className="grid grid-cols-3 grid-rows-1 gap-4 m-auto w-10/12">
                <div className="flex flex-col items-center bg-white	rounded-lg p-11">
                    <h1 className="text-xl">{pokemon.name.fr}</h1>
                    <div className="bg-white rounded-lg mb-4 text-center">
                        <img 
                            className="w-full mb-8"
                            src={imgPath} 
                            alt={pokemon.name.fr} 
                        />
                        <p className="font-medium text-lg mb-4">{pokemon.category}</p>    
                        <div className="flex justify-center gap-2">
                            {pokemon.types.map((type, index) => (
                                    <PokeType key={index} type={type} size="large"/>
                            ))}
                        </div>      
                    </div>
                    <div className="w-full mt-4 bg-slate-200 p-4 rounded-lg">
                        <p className="font-medium">Capture : {pokemon.catch_rate}%</p>
                        <p className="font-medium">Taille : {pokemon.height}</p>
                        <p className="font-medium">Poid : {pokemon.weight}</p>
                    </div>
                </div>
                <div className="col-span-2 bg-white rounded-lg p-11">
                    <h2 className="mb-4">Statistiques</h2>
                    <div className="grid grid-cols-2 bg-slate-200 p-4 rounded-lg">
                        <div>
                            <p>Attaque :</p>
                            <p>{pokemon.stats.atk}</p>
                        </div>

                        <p>Défense : {pokemon.stats.def}</p>
                        <p>Vie : {pokemon.stats.hp}</p>
                        <p>Spe-Attaque : {pokemon.stats.spe_atk}</p>
                        <p>Spe-Défense : {pokemon.stats.spe_def}</p>
                        <p>Vitesse : {pokemon.stats.vit}</p>
                    </div>
                </div>
            </div>

        </div>

    )
}

export default PokemonPage;
