import { useContext } from "react";
import { useParams } from "react-router-dom";
import { PokemonContext } from "../data/pokeContext";
import PokeType from "../components/pokeType";


function PokemonPage() {
    const data = useContext(PokemonContext);
    const {id} = useParams();

    const pokemon = data[id - 1];
    const imgPath = pokemon.sprites.regular

    console.log(pokemon);

    return(
        <div className="w-screen h-screen">
            <div className="grid grid-cols-3 grid-rows-1 gap-4 m-auto w-3/4">
                <div className="flex flex-col items-center bg-slate-200	rounded-lg p-8">
                    <h1 className="text-xl">{pokemon.name.fr}</h1>
                    <div className="bg-white rounded-lg p-4">
                        <img 
                            className="size-80"
                            src={imgPath} 
                            alt={pokemon.name.fr} 
                        />
                    </div>

                    <div className="mt-8">
                        <p className="text-sm">{pokemon.category}</p>
                    </div>
                    <div className="w-full mt-8">
                        <h2>Types</h2>
                        <div>
                            {pokemon.types.map((type, index) => (
                                    <PokeType key={index} type={type}/>
                            ))}
                        </div>
                    </div>
                </div>
                <div className="col-span-2 bg-slate-200	">
                    <h2>Infos</h2>
                </div>
            </div>

        </div>

    )
}

export default PokemonPage;
