import React, { useEffect, useState } from "react";
import PokeCard from "./components/card";

function App() {
  const [pokemons, setPokemons] = useState([]);


  useEffect(() => {
    const fetchPokemons = async () => {
      const response = await fetch('https://tyradex.tech/api/v1/gen/1');
      const pokeData = await response.json();
      setPokemons(pokeData);
    };
    fetchPokemons();
  }, []);

  return (
    <div className="flex justify-center bg-[url('./assets/pokeBg.jpg')] bg-cover bg-no-repeat bg-fixed">
      <div className="grid grid-cols-3 gap-8 max-w-7xl">
        {pokemons.map((pokemon, index) => (
            <PokeCard id={index} data={pokemon} />
        ))}
      </div>
    </div>
  );
}

export default App;
